    parseresult=CWMP.Protocol.Parser.parse(@sample)
    assert(parseresult == @sample_result)

    xml=case parseresult do
      {:ok,pres} -> case Base.decode64(hd(hd(pres.entries).voucherlist)) do
        {:ok,x} -> x
        _ -> "fail decode"
      end
      _ -> "fail"
    end
    assert(xml == @sample_xml)
  end

